  static bool
  create_schema (database& db, unsigned short pass, bool drop)
  {
    ODB_POTENTIALLY_UNUSED (db);
    ODB_POTENTIALLY_UNUSED (pass);
    ODB_POTENTIALLY_UNUSED (drop);

    if (drop)
    {
      switch (pass)
      {
        case 1:
        {
          return true;
        }
        case 2:
        {
          db.execute ("DROP TABLE IF EXISTS \"Worker\"");
          db.execute ("DROP TABLE IF EXISTS \"Project\"");
          db.execute ("DROP TABLE IF EXISTS \"Task\"");
          db.execute ("DROP TABLE IF EXISTS \"User_roles\"");
          db.execute ("DROP TABLE IF EXISTS \"User\"");
          db.execute ("DROP TABLE IF EXISTS \"Role_perms\"");
          db.execute ("DROP TABLE IF EXISTS \"Role\"");
          db.execute ("CREATE TABLE IF NOT EXISTS \"schema_version\" (\n"
                      "  \"name\" TEXT NOT NULL PRIMARY KEY,\n"
                      "  \"version\" INTEGER NOT NULL,\n"
                      "  \"migration\" INTEGER NOT NULL)");
          db.execute ("DELETE FROM \"schema_version\"\n"
                      "  WHERE \"name\" = ''");
          return false;
        }
      }
    }
    else
    {
      switch (pass)
      {
        case 1:
        {
          db.execute ("CREATE TABLE \"Role\" (\n"
                      "  \"name\" TEXT NOT NULL PRIMARY KEY)");
          db.execute ("CREATE TABLE \"Role_perms\" (\n"
                      "  \"object_id\" TEXT NOT NULL,\n"
                      "  \"value\" INTEGER NOT NULL,\n"
                      "  CONSTRAINT \"object_id_fk\"\n"
                      "    FOREIGN KEY (\"object_id\")\n"
                      "    REFERENCES \"Role\" (\"name\")\n"
                      "    ON DELETE CASCADE)");
          db.execute ("CREATE INDEX \"Role_perms_object_id_i\"\n"
                      "  ON \"Role_perms\" (\"object_id\")");
          db.execute ("CREATE TABLE \"User\" (\n"
                      "  \"id\" TEXT NOT NULL PRIMARY KEY)");
          db.execute ("CREATE TABLE \"User_roles\" (\n"
                      "  \"object_id\" TEXT NOT NULL,\n"
                      "  \"value\" TEXT NOT NULL,\n"
                      "  CONSTRAINT \"object_id_fk\"\n"
                      "    FOREIGN KEY (\"object_id\")\n"
                      "    REFERENCES \"User\" (\"id\")\n"
                      "    ON DELETE CASCADE,\n"
                      "  CONSTRAINT \"value_fk\"\n"
                      "    FOREIGN KEY (\"value\")\n"
                      "    REFERENCES \"Role\" (\"name\")\n"
                      "    DEFERRABLE INITIALLY DEFERRED)");
          db.execute ("CREATE INDEX \"User_roles_object_id_i\"\n"
                      "  ON \"User_roles\" (\"object_id\")");
          db.execute ("CREATE TABLE \"Task\" (\n"
                      "  \"id\" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,\n"
                      "  \"name\" TEXT NOT NULL,\n"
                      "  \"project\" INTEGER NOT NULL,\n"
                      "  \"worker\" INTEGER NULL,\n"
                      "  CONSTRAINT \"project_fk\"\n"
                      "    FOREIGN KEY (\"project\")\n"
                      "    REFERENCES \"Project\" (\"id\")\n"
                      "    DEFERRABLE INITIALLY DEFERRED,\n"
                      "  CONSTRAINT \"worker_fk\"\n"
                      "    FOREIGN KEY (\"worker\")\n"
                      "    REFERENCES \"Worker\" (\"id\")\n"
                      "    DEFERRABLE INITIALLY DEFERRED)");
          db.execute ("CREATE TABLE \"Project\" (\n"
                      "  \"id\" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,\n"
                      "  \"name\" TEXT NOT NULL)");
          db.execute ("CREATE TABLE \"Worker\" (\n"
                      "  \"id\" INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT,\n"
                      "  \"name\" TEXT NOT NULL)");
          return true;
        }
        case 2:
        {
          db.execute ("CREATE TABLE IF NOT EXISTS \"schema_version\" (\n"
                      "  \"name\" TEXT NOT NULL PRIMARY KEY,\n"
                      "  \"version\" INTEGER NOT NULL,\n"
                      "  \"migration\" INTEGER NOT NULL)");
          db.execute ("INSERT OR IGNORE INTO \"schema_version\" (\n"
                      "  \"name\", \"version\", \"migration\")\n"
                      "  VALUES ('', 1, 0)");
          return false;
        }
      }
    }

    return false;
  }

